import React, { Component } from "react";
import { connect } from "react-redux";

class loginDropdown extends Component {
  constructor(props) {
    super(props);

    this.state = { displayMenu: false };

    // This binding is necessary to make `this` work in the callback
    this.showDropdownMenu = this.showDropdownMenu.bind(this);
    this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
  }

  showDropdownMenu(event) {
    event.preventDefault();
    this.setState({ displayMenu: true }, () => {
      document.addEventListener("click", this.hideDropdownMenu);
    });
  }

  hideDropdownMenu() {
    this.setState({ displayMenu: false }, () => {
      document.removeEventListener("click", this.hideDropdownMenu);
    });
  }

  render() {
    //console.log(this.props.auth.DisplayName);

    switch (this.props.auth) {
      case null:
        return;
      case false:
        return;
      default:
        return (
          <div
            className={
              "dropdown-trigger btn" + (this.state.displayMenu ? " show" : "")
            }
          >
            <div
              className={
                "dropdown-display" + (this.state.displayMenu ? " clicked" : "")
              }
              onClick={this.showDropdownMenu}
            >
              <img src={this.props.auth.Picture} alt="new" />
            </div>
            <div className="dropdown-list" style={{ width: "150px", margin: "0 10px" }}>
              <div className="right teal lighten-">
                {this.renderListItems()}
              </div>
            </div>
          </div>
        );
    }
  }

  renderListItems() {
    if (this.state.displayMenu) {
      return [
        <div key="1" style={{ margin: "0 10px" }}>
          {this.props.auth.DisplayName}
        </div>,
        <div key="3" style={{ margin: "0 10px" }}>
          Credits: {this.props.auth.credits}
        </div>,
        <div key="4" style={{ margin: "0 10px" }}>
          <a href="/api/logout">Logout</a>
        </div>
      ];
    }
  }
}

function mapStateToProps(state) {
  return {
    auth: state.auth
  };
}

export default connect(mapStateToProps)(loginDropdown);
