import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import Payments from "./Payments";

const colours = [
  {
    name: "Red",
    hex: "#F21B1B"
  },
  {
    name: "Blue",
    hex: "#1B66F2"
  },
  {
    name: "Green",
    hex: "#07BA16"
  }
];

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = { displayMenu: true };

    // This binding is necessary to make `this` work in the callback
    this.showDropdownMenu = this.showDropdownMenu.bind(this);
    this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
  }

  showDropdownMenu(event) {
    event.preventDefault();
    this.setState({ displayMenu: true }, () => {
      document.addEventListener("click", this.hideDropdownMenu);
    });
  }

  hideDropdownMenu() {
    this.setState({ displayMenu: false }, () => {
      document.removeEventListener("click", this.hideDropdownMenu);
    });
  }

  renderDropdown() {
    console.log(this.state.isToggleOn);

    return (
      <div
        className={
          "dropdown-container" + (this.state.displayMenu ? " show" : "")
        }
      >
        <div
          className={
            "dropdown-display" + (this.state.displayMenu ? " clicked" : "")
          }
          onClick={this.showDropdownMenu}
        >
          <i className="fa fa-angle-down" />
        </div>
        <div className="dropdown-list">
          <div>{this.renderListItems()}</div>
        </div>
      </div>
    );
  }

  renderListItems() {
    var items = [];
    for (var i = 0; i < colours.length; i++) {
      var item = colours[i];
      items.push(
        <div onClick={this.select.bind(null, item)}>
          <span style={{ color: item.hex }}>{item.name}</span>
        </div>
      );
    }
    return items;
  }

  renderContent() {
    switch (this.props.auth) {
      case null:
        return;
      case false:
        return (
          <li>
            <a href="/auth/google" style={{ margin: "0 10px" }}>
              Login With Google
            </a>
          </li>
        );
      default:
        return [
          <li key="0" style={{ margin: "10px 10px" }}>
            <img src={this.props.auth.Picture} alt="new" />
          </li>,
          <li key="1" style={{ margin: "0 10px" }}>
            {this.props.auth.DisplayName}
          </li>,
          <li key="2" style={{ margin: "0 10px" }}>
            <Payments />
          </li>,
          <li key="3" style={{ margin: "0 10px" }}>
            Credits: {this.props.auth.credits}
          </li>,
          <li key="4" style={{ margin: "0 10px" }}>
            <a href="/api/logout">Logout</a>
          </li>,
          <li key="5" style={{ margin: "0 10px" }}>
            {this.renderDropdown()}
          </li>
        ];
    }
  }

  render() {
    //console.log(this.props);

    return (
      <nav>
        <div className="nav-wrapper">
          <Link
            to={this.props.auth ? "/surveys" : "/"}
            className="left brand-logo"
            style={{ margin: "0 10px" }}
          >
            Emaily
          </Link>
          <ul id="nav-mobile" className="right hide-on-med-and-down">
            {this.renderContent()}
          </ul>
        </div>
      </nav>
    );
  }
}

function mapStateToProps(state) {
  return {
    auth: state.auth
  };
}

export default connect(mapStateToProps)(Header);
