import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import Payments from "./Payments";

class Header extends Component {
  constructor() {
    super();

    this.state = {
      displayMenu: false
    };

    this.showDropdownMenu = this.showDropdownMenu.bind(this);
    this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
  }

  showDropdownMenu(event) {
    event.preventDefault();
    this.setState({ displayMenu: true }, () => {
      document.addEventListener("click", this.hideDropdownMenu);
    });
  }

  hideDropdownMenu() {
    this.setState({ displayMenu: false }, () => {
      document.removeEventListener("click", this.hideDropdownMenu);
    });
  }

  renderDropdown(){
    return (
      <div className="dropdown" style={{ background: "red", width: "100px", margin: "10px 10px" }}>
        <div className="button" onClick={this.showDropdownMenu}>
          {" "}
          My Setting{" "}
        </div>

        {this.state.displayMenu ? (
          <ul>
            <li>
              <a className="active" href="#Create Page">
                Create Page
              </a>
            </li>
            <li>
              <a href="#Manage Pages">Manage Pages</a>
            </li>
            <li>
              <a href="#Create Ads">Create Ads</a>
            </li>
            <li>
              <a href="#Manage Ads">Manage Ads</a>
            </li>
            <li>
              <a href="#Activity Logs">Activity Logs</a>
            </li>
            <li>
              <a href="#Setting">Setting</a>
            </li>
            <li>
              <a href="#Log Out">Log Out</a>
            </li>
          </ul>
        ) : null}
      </div>
    );
  }


  renderContent() {
    switch (this.props.auth) {
      case null:
        return;
      case false:
        return (
          <li>
            <a href="/auth/google" style={{ margin: "0 10px" }}>
              Login With Google
            </a>
          </li>
        );
      default:
        return [
          <li key="0" style={{ margin: "10px 10px" }}>
            <img src={this.props.auth.Picture} alt="new" />
          </li>,
          <li key="1" style={{ margin: "0 10px" }}>
            {this.props.auth.DisplayName}
          </li>,
          <li key="2" style={{ margin: "0 10px" }}>
            <Payments />
          </li>,
          <li key="3" style={{ margin: "0 10px" }}>
            Credits: {this.props.auth.credits}
          </li>,
          <li key="4" style={{ margin: "0 10px" }}>
            <a href="/api/logout">Logout</a>
          </li>,
          <li key="5" style={{ margin: "0 10px" }}>
          {this.renderDropdown()}
          </li>
        ];
    }
  }

  render() {
    console.log(this.props);
    return (
      <nav>
        <div className="nav-wrapper">
          <Link
            to={this.props.auth ? "/surveys" : "/"}
            className="left brand-logo"
            style={{ margin: "0 10px" }}
          >
            Emaily
          </Link>
          <ul id="nav-mobile" className="right hide-on-med-and-down">
            {this.renderContent()}
          </ul>
        </div>
      </nav>
    );
  }
}

function mapStateToProps(state) {
  return {
    auth: state.auth
  };
}

export default connect(mapStateToProps)(Header);
